INSERT INTO `pages_en` VALUES
(1, 'main page', '<h1 style=\"vertical-align: bottom;\"><img style=\"vertical-align: bottom;\" title=\"Main\" onmouseover=\"this.src=\'/images/icons/home.a.gif\' \" onmouseout=\"this.src=\'/images/icons/home.gif\'\" src=\"../../../../../images/icons/home.gif\" alt=\"Main\" width=\"32\" height=\"32\" align=\"middle\" />&nbsp;<span style=\"font-size: x-large; font-family: tahoma, arial, helvetica, sans-serif;\">Main</span></h1>\r\n<p><span style=\"font-size: small;\"><strong><span style=\"font-size: medium;\"><span class=\"hps\">Welcome to</span>&nbsp;<span class=\"hps\">Crimea Eco Grid -</span>&nbsp;<span class=\"hps\">Portal</span>!</span><br /><br /><span style=\"font-size: medium;\"><span class=\"hps\">This system</span>&nbsp;<span class=\"hps\">was specifically designed</span>&nbsp;<span class=\"hps\">to</span>&nbsp;<span class=\"hps\">simplify and expedite</span></span><br /><br /><span style=\"font-size: medium;\"><span class=\"hps\">calculations</span>&nbsp;<span class=\"hps\">the researchers creating</span>&nbsp;<span class=\"hps\">their tasks in</span>&nbsp;<span class=\"hps\">the Grid</span>!</span><br /><br /><span style=\"font-size: medium;\"><span class=\"hps\">The portal</span>&nbsp;<span class=\"hps\">has a number</span>&nbsp;<span class=\"hps\">of <a title=\"advantages\" href=\"en/page/advantages\">advantages</a></span>&nbsp;<span class=\"hps\">and may be useful</span>&nbsp;<span class=\"hps\"><span class=\"hps alt-edited\">both for beginners</span><span>&nbsp;</span></span><span class=\"hps\">and</span></span><br /><br /><span style=\"font-size: medium;\"><span class=\"hps\">professional researchers</span>.</span><br /><br /><span style=\"font-size: small;\"><span class=\"hps\">The following</span>&nbsp;<span class=\"hps\">will list</span>&nbsp;<span class=\"hps\">the main blocks</span>&nbsp;<span class=\"hps\">of Grid</span>&nbsp;<span class=\"hps\">Portal</span>:</span><br /></strong></span></p>\r\n<ul class=\"list-stic\">\r\n<li> <a class=\"href1\" title=\"preparing\" href=\"en/profile/edit\"><span>preparing</span></a> </li>\r\n<li> <a class=\"href2\" title=\"projects\" href=\"en/project/list\"><span>projects</span></a> </li>\r\n<br />\r\n<li> <a class=\"href3\" title=\"tasks\" href=\"en/task-set\"><span>tasks</span></a> </li>\r\n<li> <a class=\"href4\" title=\"results\" href=\"en/task-submit/analyze\"><span>results</span></a> </li>\r\n<br />\r\n<li> <a class=\"href5\" title=\"statistics\" href=\"en/task-set/statistics\"><span>statistics</span></a> </li>\r\n</ul>', NULL, NULL),
